mapper.select:
select * from (select tup.*, case tup.recharge_type when 5 then '火箭会员' when 6 then '星球会员' when 10 then '宇宙会员' when 1 then '品牌会员' else '未知' end as rechargeTypeStr, (select tu.nickname from tb_user as tu where tup.user_id = tu.id) as userName, (select tu.wx_headimgurl from tb_user as tu where tup.user_id = tu.id) as wxHeadImgurl, tmp_tsp1.user_id as sp1Id, tmp_tsp1.nickname as sp1Name,tmp_tsp1.profit as sp1Profit, tmp_tsp2.user_id as sp2Id, tmp_tsp2.nickname as sp2Name,tmp_tsp2.profit as sp2Profit from tb_user_payment as tup left join (select tu.nickname, tu.id, tsp.user_id, tsp.paymnet_id, tsp.profit from tb_share_profit as tsp inner join tb_user as tu on tu.id = tsp.user_id where tsp.sub_type = 1) as tmp_tsp1 on tup.id = tmp_tsp1.paymnet_id
left join (select tu.nickname, tu.id, tsp.user_id, tsp.paymnet_id, tsp.profit from tb_share_profit as tsp inner join tb_user as tu on tu.id = tsp.user_id where tsp.sub_type = 2) as tmp_tsp2 on tup.id = tmp_tsp2.paymnet_id ) as tmp
where 1=1 and status = 'paid'
AND tmp.user_id = #{userId}
AND tmp.recharge_type = #{rechargeType}
AND tmp.userName like #{_userName}
AND tmp.sp1Id = #{sp1Id}
AND tmp.sp1Name like #{_sp1Name}
AND tmp.gmt_create >= str_to_date(#{sTime}, '%Y-%m-%d %H:%i:%s')
AND tmp.gmt_create <= str_to_date(#{eTime}, '%Y-%m-%d %H:%i:%s')
order by tmp.gmt_create desc

select count(*) from tb_user_payment as tup where tup.cdk_code != '' and tup.cdk_code in (select code from tb_brand_cdkey where brand_id in
#{brandId}
and is_used = 1)
